#!/usr/bin/env node

// Troubleshooting script for Seana Bot deployment issues
const fs = require('fs');
const path = require('path');

console.log('ğŸ”§ Seana Bot Troubleshooting Tool');
console.log('==================================\n');

// Check Node.js version
console.log('ğŸ“‹ System Check:');
console.log(`   Node.js version: ${process.version}`);
console.log(`   Platform: ${process.platform}`);
console.log(`   Architecture: ${process.arch}\n`);

// Check if Node.js version meets requirements
const nodeVersion = parseInt(process.version.slice(1).split('.')[0]);
if (nodeVersion < 20) {
    console.log('âŒ Node.js version check FAILED');
    console.log('   @whiskeysockets/baileys requires Node.js 20+');
    console.log(`   Current version: ${process.version}`);
    console.log('   Please upgrade to Node.js 20+\n');
} else {
    console.log('âœ… Node.js version check PASSED\n');
}

// Check package.json
console.log('ğŸ“¦ Package Check:');
try {
    const packageJson = JSON.parse(fs.readFileSync('package.json', 'utf8'));

    // Check engines field
    if (packageJson.engines && packageJson.engines.node) {
        console.log(`   Engines.node: ${packageJson.engines.node}`);
    } else {
        console.log('   âš ï¸  No engines.node field found in package.json');
    }

    // Check baileys version
    if (packageJson.dependencies && packageJson.dependencies['@whiskeysockets/baileys']) {
        console.log(`   Baileys version: ${packageJson.dependencies['@whiskeysockets/baileys']}`);
    }

    console.log('âœ… package.json check PASSED\n');
} catch (error) {
    console.log('âŒ package.json check FAILED');
    console.log(`   Error: ${error.message}\n`);
}

// Check required files
console.log('ğŸ“ File Check:');
const requiredFiles = [
    'index.js',
    'server.js',
    'config/config.js',
    'database/connection.js',
    'Dockerfile',
    'railway.json',
    '.dockerignore'
];

const missingFiles = [];
requiredFiles.forEach(file => {
    if (fs.existsSync(file)) {
        console.log(`   âœ… ${file}`);
    } else {
        console.log(`   âŒ ${file} - MISSING`);
        missingFiles.push(file);
    }
});

if (missingFiles.length > 0) {
    console.log(`\n   âš ï¸  Missing ${missingFiles.length} required files`);
}
console.log();

// Check Dockerfile
console.log('ğŸ³ Dockerfile Check:');
try {
    const dockerfile = fs.readFileSync('Dockerfile', 'utf8');

    if (dockerfile.includes('node:20')) {
        console.log('   âœ… Using Node.js 20');
