    description: 'Check bot response time and status',
    usage: '.ping',
    category: 'general',
    requiresLimit: false,
    execute: pingCommand
};

commands/profile.js.content:
const config = require('../config/config');
const { getUser } = require('../utils/userUtils');

const profileCommand = async (sock, from, sender, args, message) => {
    try {
        // Get user data
        const user = await getUser(sender);
        if (!user) {
            await sock.sendMessage(from, { 
                text: config.messages.databaseError 
            });
            return false;
        }

        // Check if user has enough limits
        if (user.limit < 1) {
            await sock.sendMessage(from, { 
                text: config.messages.insufficientLimit 
            });
            return false;
        }

        // Get WhatsApp profile name from multiple sources
        let whatsappName = 'User';

        // Try to get name from message pushName first
        if (message && message.pushName) {
            whatsappName = message.pushName;
        } else {
            // Try other methods
            try {
                // Try getting contact info
                const contactInfo = await sock.onWhatsApp(sender);
                if (contactInfo && contactInfo[0] && contactInfo[0].notify) {
                    whatsappName = contactInfo[0].notify;
                }
            } catch (error) {
                console.log('Could not fetch WhatsApp name from onWhatsApp');
            }

            // If still no name, try getting from chat
            if (whatsappName === 'User') {
                try {
                    const chat = await sock.chatRead(from);
                    if (chat && chat.name) {
                        whatsappName = chat.name;
                    }
                } catch (error) {
                    console.log('Could not fetch name from chat');
                }
            }
        }

        console.log(`ğŸ“ Using WhatsApp name: ${whatsappName} for user: ${sender}`);

        // Deduct limit silently (no notification)
        user.deductLimit(1);
        user.updateActivity();
        await user.save();

        // Format limit display based on user status
        let limitDisplay = '';
        if (user.hasUnlimitedLimits()) {
            limitDisplay = 'âˆ (unlimited)';
        } else {
            limitDisplay = `${user.limit}/${user.totalLimit}`;
        }

        // Format profile message
        const profileText = config.messages.profileMessage
            .replace('{username}', whatsappName)
            .replace('{tag}', `@${sender.split('@')[0]}`)
            .replace('{status}', user.status.toUpperCase())
            .replace('{limitDisplay}', limitDisplay)
            .replace('{balance}', user.balance)
            .replace('{chips}', user.chips)
            .replace('{memberSince}', user.getFormattedMemberSince());

        // Get profile picture
        let profilePicUrl = null;
        try {
            profilePicUrl = await sock.profilePictureUrl(sender, 'image');
        } catch (error) {
            console.log('Could not fetch profile picture');
        }

        // Send profile info with picture if available
        if (profilePicUrl) {
            await sock.sendMessage(from, {
                image: { url: profilePicUrl },
                caption: profileText,
                mentions: [sender]
            });
        } else {
            // Send without image if profile picture is not available
            await sock.sendMessage(from, { 
                text: profileText,
                mentions: [sender]
            });
        }

        return true;
    } catch (error) {
        console.error('âŒ Error in profile command:', error.message);
        await sock.sendMessage(from, { 
            text: config.messages.databaseError 
        });
        return false;
    }
};

module.exports = {
    name: 'profile',
    description: 'View your user profile and statistics',
    usage: '.profile',
    category: 'user',
    requiresLimit: true,
    limitCost: 1,
    execute: profileCommand
};

commands/sticker.js.content:
const config = require('../config/config');
const { createSticker, downloadMedia, isImageMessage, getImageMessage } = require('../utils/stickerUtils');

const stickerCommand = async (sock, from, sender, args, message) => {
    try {
        // Check if it's a reply to an image message
        const quotedMessage = message.message?.extendedTextMessage?.contextInfo?.quotedMessage;
        let targetMessage = null;

        if (quotedMessage) {
            // It's a reply - check if quoted message has image
            if (quotedMessage.imageMessage || 
                quotedMessage.viewOnceMessage?.message?.imageMessage ||
                quotedMessage.ephemeralMessage?.message?.imageMessage) {

                // Create a message object for the quoted content
                targetMessage = {
                    message: quotedMessage,
                    key: message.message.extendedTextMessage.contextInfo.participant 
                        ? { participant: message.message.extendedTextMessage.contextInfo.participant }
                        : { remoteJid: from }
                };
            }
        } else {
            // Check if current message has image
            if (isImageMessage(message)) {
                targetMessage = message;
            }
        }

        // If no image found
        if (!targetMessage) {
            await sock.sendMessage(from, {
                text: 'âŒ Penggunaan salah!\n\n' +
                      'ğŸ“ Cara Penggunaan:\n' +
                      'â€¢ Reply ke gambar: .s / .sticker\n' +
                      'â€¢ Kirim gambar: .s / .sticker \n' 
            });
            return false;
        }

        // Send processing message
        await sock.sendMessage(from, {
            text: 'â³ Sedang membuat sticker... Tunggu bentar yaa!'
        });

        try {
            // Download the image
            console.log('ğŸ“¥ Downloading image for sticker conversion...');
            const imageBuffer = await downloadMedia(targetMessage);

            if (!imageBuffer) {
                throw new Error('Failed to download image');
            }

            console.log('ğŸ¨ Converting image to sticker...');
            // Create sticker
            const stickerBuffer = await createSticker(
                imageBuffer, 
                config.sticker.pack, 
                config.sticker.author
            );

            // Send sticker 
            console.log('ğŸ“¤ Sending sticker...');
            await sock.sendMessage(from, {
                sticker: stickerBuffer
            });

            console.log('âœ… Sticker sent successfully');

        } catch (conversionError) {
            console.error('âŒ Sticker conversion error:', conversionError.message);

            await sock.sendMessage(from, {
                text: 'âŒ Failed to create sticker. Please make sure:\n' +
                      'â€¢ The image is valid (JPG, PNG, WebP)\n' +
                      'â€¢ The image is not corrupted\n' +
                      'â€¢ Try with a different image'
            });
            return false;
        }

        return true;

    } catch (error) {
        console.error('âŒ Sticker command error:', error.message);

        await sock.sendMessage(from, {
            text: 'âŒ An error occurred while processing the sticker. Please try again later.'
        });

        return false;
    }
};

module.exports = {
    name: 'sticker',
    aliases: ['s'],
    description: 'Convert image to sticker (1 limit)',
    usage: '.s/.sticker (reply to image or send with image)',
    category: 'media',
    requiresLimit: true,
    limitCost: 1,
    execute: stickerCommand
};

