const Post = require('./models/Post');
const connectDB = require('./connection');

// Initialize posts collection
const initPostsTable = async () => {
    try {
        await connectDB();
        console.log('âœ… Posts system initialized');
    } catch (error) {
        console.error('âŒ Failed to initialize posts system:', error);
        throw error;
    }
};

// Get recent posts for social feed
const getRecentPosts = async (limit = 20) => {
    try {
        const posts = await Post.getRecentPosts(limit);
        return posts;
    } catch (error) {
        console.error('âŒ Error fetching recent posts:', error);
        return [];
    }
};

// Get posts by specific user
const getPostsByUser = async (userId, limit = 20) => {
    try {
        const posts = await Post.getPostsByUser(userId, limit);
        return posts;
    } catch (error) {
        console.error('âŒ Error fetching user posts:', error);
        return [];
    }
};

// Create new post
const createPost = async (userId, author, type, content, mediaUrl = '') => {
    try {
        const newPost = new Post({
            userId,
            author,
            type,
            content,
            mediaUrl
        });

        await newPost.save();
        console.log('âœ… Post created successfully');
        return newPost;
    } catch (error) {
        console.error('âŒ Error creating post:', error);
        throw error;
    }
};

// Update post
const updatePost = async (postId, updates) => {
    try {
        const updatedPost = await Post.findByIdAndUpdate(
            postId, 
            updates, 
            { new: true }
        );

        if (!updatedPost) {
            console.log('âš ï¸ Post not found for update');
            return null;
        }

        console.log('âœ… Post updated successfully');
        return updatedPost;
    } catch (error) {
        console.error('âŒ Error updating post:', error);
        throw error;
    }
};

// Delete post
const deletePost = async (postId) => {
    try {
        const deletedPost = await Post.findByIdAndDelete(postId);

        if (!deletedPost) {
            console.log('âš ï¸ Post not found for deletion');
            return false;
        }

        console.log('âœ… Post deleted successfully');
        return true;
    } catch (error) {
        console.error('âŒ Error deleting post:', error);
        throw error;
    }
};

// Like post
const likePost = async (postId, userId) => {
    try {
        const post = await Post.findById(postId);
        if (!post) {
            return { success: false, message: 'Post not found' };
        }

        const liked = post.addLike(userId);
        if (liked) {
            await post.save();
            return { success: true, message: 'Post liked', likesCount: post.getLikesCount() };
        } else {
            return { success: false, message: 'Already liked' };
        }
    } catch (error) {
        console.error('âŒ Error liking post:', error);
        throw error;
    }
};

// Unlike post
const unlikePost = async (postId, userId) => {
    try {
        const post = await Post.findById(postId);
        if (!post) {
            return { success: false, message: 'Post not found' };
        }

        const unliked = post.removeLike(userId);
        if (unliked) {
            await post.save();
            return { success: true, message: 'Post unliked', likesCount: post.getLikesCount() };
        } else {
            return { success: false, message: 'Not liked yet' };
        }
    } catch (error) {
        console.error('âŒ Error unliking post:', error);
        throw error;
    }
};

// Add comment to post
const addComment = async (postId, userId, author, content) => {
    try {
        const post = await Post.findById(postId);
        if (!post) {
            return { success: false, message: 'Post not found' };
        }

        post.addComment(userId, author, content);
        await post.save();

        return { 
            success: true, 
            message: 'Comment added', 
            commentsCount: post.getCommentsCount() 
        };
    } catch (error) {
        console.error('âŒ Error adding comment:', error);
        throw error;
    }
};

module.exports = {
    initPostsTable,
    getRecentPosts,
    getPostsByUser,
    createPost,
    updatePost,
    deletePost,
    likePost,
    unlikePost,
    addComment
};

database/shop.js.content:
const mongoose = require('mongoose');

// Shop Item Schema
const shopItemSchema = new mongoose.Schema({
    name: {
        type: String,
        required: true
    },
    description: {
        type: String,
        default: ''
    },
    category: {
        type: String,
        required: true,
        enum: ['border', 'premium', 'booster', 'cosmetic']
    },
    price: {
        type: Number,
        required: true,
        min: 0
    },
    priceType: {
        type: String,
        required: true,
        enum: ['balance', 'chips']
    },
    stock: {
        type: Number,
        required: true,
        min: 0,
        default: 0
    },
    imageUrl: {
        type: String,
        default: ''
    },
    isActive: {
        type: Boolean,
        default: true
    },
    purchaseLimit: {
        type: String,
        enum: ['unlimited', 'once'],
        default: 'unlimited'
    },
    createdAt: {
        type: Date,
        default: Date.now
    },
    createdBy: {
        type: String,
        default: 'system'
    }
});

// Purchase History Schema
const purchaseHistorySchema = new mongoose.Schema({
    userId: {
        type: String,
        required: true
    },
    itemId: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'ShopItem',
        required: true
    },
    itemName: {
        type: String,
        required: true
    },
    price: {
        type: Number,
        required: true
    },
    priceType: {
        type: String,
        required: true
    },
    purchasedAt: {
        type: Date,
        default: Date.now
    }
});

const ShopItem = mongoose.model('ShopItem', shopItemSchema);
const PurchaseHistory = mongoose.model('PurchaseHistory', purchaseHistorySchema);

// Initialize shop table
const initShopTable = async () => {
    try {
        console.log('ğŸ›’ Initializing shop system...');

        // Check if shop items already exist
        const existingItems = await ShopItem.countDocuments();
        if (existingItems > 0) {
            console.log('âœ… Shop system already initialized');
            return;
        }

        // Create default shop items
        const defaultItems = [
            {
                name: 'Gold Frame Border',
                description: 'Elegant gold border frame for your profile',
                category: 'border',
                price: 5000,
                priceType: 'balance',
                stock: 50,
                imageUrl: '/borders/gold-frame.svg'
            },
            {
                name: 'Neon Glow Border',
                description: 'Futuristic neon glow effect border',
                category: 'border',
                price: 2000,
                priceType: 'chips',
                stock: 30,
                imageUrl: '/borders/neon-glow.svg'
            },
            {
                name: 'Diamond Luxury Border',
                description: 'Premium diamond-studded border',
                category: 'border',
                price: 10000,
                priceType: 'chips',
                stock: 10,
                imageUrl: '/borders/diamond-luxury.svg'
            }
        ];

        await ShopItem.insertMany(defaultItems);
        console.log('âœ… Default shop items created successfully');

    } catch (error) {
        console.error('âŒ Error initializing shop system:', error.message);
    }
};

// Get all shop items
const getAllShopItems = async () => {
    try {
        return await ShopItem.find({ isActive: true }).sort({ createdAt: -1 });
    } catch (error) {
        console.error('âŒ Error fetching shop items:', error.message);
        return [];
    }
};

// Get shop item by ID
const getShopItemById = async (itemId) => {
    try {
        return await ShopItem.findById(itemId);
    } catch (error) {
        console.error('âŒ Error fetching shop item:', error.message);
        return null;
    }
};

// Create new shop item
const createShopItem = async (itemData) => {
    try {
        const newItem = new ShopItem(itemData);
        await newItem.save();
        console.log(`âœ… Shop item created: ${newItem.name}`);
        return newItem;
    } catch (error) {
        console.error('âŒ Error creating shop item:', error.message);
        return null;
    }
};

// Update shop item
const updateShopItem = async (itemId, updateData) => {
    try {
        const updatedItem = await ShopItem.findByIdAndUpdate(
            itemId, 
            updateData, 
            { new: true, runValidators: true }
        );

        if (updatedItem) {
            console.log(`âœ… Shop item updated: ${updatedItem.name}`);
        }

        return updatedItem;
    } catch (error) {
        console.error('âŒ Error updating shop item:', error.message);
        return null;
    }
};

// Delete shop item
const deleteShopItem = async (itemId) => {
    try {
        const deletedItem = await ShopItem.findByIdAndUpdate(
            itemId,
            { isActive: false },
            { new: true }
        );

        if (deletedItem) {
            console.log(`âœ… Shop item deleted: ${deletedItem.name}`);
        }

        return deletedItem;
    } catch (error) {
        console.error('âŒ Error deleting shop item:', error.message);
        return null;
    }
};

// Purchase item
const purchaseItem = async (userId, itemId) => {
    try {
        const item = await getShopItemById(itemId);
        if (!item) {
            return { success: false, message: 'Item tidak ditemukan' };
        }

        if (item.stock <= 0) {
            return { success: false, message: 'Item sudah habis' };
        }

        // Get user data
        const { getUser, updateUser } = require('../utils/userUtils');
        const user = await getUser(userId);

        if (!user) {
            return { success: false, message: 'User tidak ditemukan' };
        }

        // Check if item is one-time purchase and user already bought it
        if (item.purchaseLimit === 'once') {
            const existingPurchase = await PurchaseHistory.findOne({ userId, itemId });
            if (existingPurchase) {
                return { success: false, message: 'Item ini hanya bisa dibeli sekali per akun' };
            }
        }

        // Check if user has enough balance/chips
        const userAmount = item.priceType === 'balance' ? user.balance : user.chips;
        if (userAmount < item.price) {
            return { success: false, message: `${item.priceType === 'balance' ? 'Balance' : 'Chips'} tidak mencukupi` };
        }

        // Deduct user balance/chips
        const updateData = {};
        if (item.priceType === 'balance') {
            updateData.balance = user.balance - item.price;
        } else {
            updateData.chips = user.chips - item.price;
        }

        await updateUser(userId, updateData);

        // Reduce item stock
        await updateShopItem(itemId, { stock: item.stock - 1 });

        // Add item to user if it's a border
        if (item.category === 'border') {
            const { addBorderToUser } = require('./borders');
            // Create proper border ID mapping
            let borderId;
            if (item.name.toLowerCase().includes('gold')) {
                borderId = 'gold-frame';
            } else if (item.name.toLowerCase().includes('neon')) {
                borderId = 'neon-glow';
            } else if (item.name.toLowerCase().includes('diamond')) {
                borderId = 'diamond-luxury';
            } else {
                // Create ID from name
                borderId = item.name.toLowerCase().replace(/\s+/g, '-').replace(/[^a-z0-9-]/g, '');
            }

            console.log(`ğŸ›’ Adding border to user ${userId}:`, {
                itemName: item.name,
                borderId: borderId,
                category: item.category
            });

            const borderResult = await addBorderToUser(userId, borderId);
            console.log(`ğŸ›’ Border assignment result for ${userId}:`, borderResult);

            if (!borderResult.success) {
                console.error(`âŒ Failed to add border ${borderId} to user ${userId}:`, borderResult.message);
            } else {
                console.log(`âœ… Successfully added border ${borderId} to user ${userId}`);
            }
        }

        // Record purchase history
        const purchase = new PurchaseHistory({
            userId,
            itemId,
            itemName: item.name,
            price: item.price,
            priceType: item.priceType
        });

        await purchase.save();

        console.log(`âœ… Item purchased: ${item.name} by ${userId}`);

        return { 
            success: true, 
            message: 'Item berhasil dibeli!',
            item: item,
            purchase: purchase
        };

    } catch (error) {
        console.error('âŒ Error purchasing item:', error.message);
        return { success: false, message: 'Terjadi kesalahan saat membeli item' };
    }
};

// Get user purchase history
const getUserPurchaseHistory = async (userId) => {
    try {
        return await PurchaseHistory.find({ userId }).sort({ purchasedAt: -1 });
    } catch (error) {
        console.error('âŒ Error fetching purchase history:', error.message);
        return [];
    }
};

module.exports = {
    ShopItem,
    PurchaseHistory,
    initShopTable,
    getAllShopItems,
    getShopItemById,
    createShopItem,
    updateShopItem,
    deleteShopItem,
    purchaseItem,
    getUserPurchaseHistory
};

handlers/commandHandler.js.content:
const config = require('../config/config');
const { getUser, updateUserActivity } = require('../utils/userUtils');

// Import all commands
const menuCommand = require('../commands/menu');
const pingCommand = require('../commands/ping');
const profileCommand = require('../commands/profile');
const stickerCommand = require('../commands/sticker');

// Command registry
const commands = {
    'menu': menuCommand,
    'ping': pingCommand,
    'profile': profileCommand,
    'sticker': stickerCommand,
    's': stickerCommand
};

const commandHandler = async (sock, from, sender, commandName, args, message) => {
    try {
        // Check if command exists
        const command = commands[commandName];
        if (!command) {
            await sock.sendMessage(from, { 
                text: config.messages.commandNotFound 
            });
            return;
        }

        console.log(`ğŸ¯ Executing command: ${commandName} for user: ${sender}`);

        // Get user data for permission checks
        const user = await getUser(sender);
        if (!user) {
            await sock.sendMessage(from, { 
                text: config.messages.databaseError 
            });
            return;
        }

        // Check if command requires limits
        const isLimitCommand = config.commands.limitCommands.includes(commandName);
        if (isLimitCommand || command.requiresLimit) {
            const limitCost = command.limitCost || 1;

            // Owner and premium users have unlimited limits
            if (!user.hasUnlimitedLimits() && user.limit < limitCost) {
                await sock.sendMessage(from, { 
                    text: config.messages.insufficientLimit 
                });
                return;
            }
        }

        // Execute the command
        const success = await command.execute(sock, from, sender, args, message);

        if (success) {
            console.log(`âœ… Command ${commandName} executed successfully`);

            // Update user activity and award XP
            const activityResult = await updateUserActivity(sender, 'commandUse');

            // Send level up notification if user leveled up
            if (activityResult && activityResult.leveledUp) {
                const rank = activityResult.currentRank;
                const levelUpMessage = `
ğŸ‰ *LEVEL UP!* ğŸ‰

${rank.icon} **${user.username}** has reached Level **${activityResult.newLevel}**!
ğŸ“ˆ Rank: **${rank.name}**
âš¡ XP Gained: +${activityResult.xpGained}

Keep using commands to earn more XP! ğŸš€`;

                setTimeout(() => {
                    sock.sendMessage(from, { text: levelUpMessage });
                }, 1000);
            }
        } else {
            console.log(`âŒ Command ${commandName} failed to execute`);
        }

    } catch (error) {
        console.error(`âŒ Error in command handler for ${commandName}:`, error.message);
        await sock.sendMessage(from, { 
            text: config.messages.databaseError 
        });
    }
};

module.exports = commandHandler;

