    line-height: 1.4;
}

.news-excerpt, .news-content {
    color: #d1d5db;
    line-height: 1.6;
    margin-bottom: 1rem;
}

.news-author {
    color: #6366f1;
    font-size: 0.85rem;
    font-weight: 500;
}

/* Featured News */
.featured-news {
    margin-bottom: 3rem;
}

.featured-card {
    background: rgba(40, 40, 40, 0.95);
    border-radius: 20px;
    padding: 2.5rem;
    border: 1px solid rgba(251, 191, 36, 0.2);
    backdrop-filter: blur(10px);
}

.featured-card .news-title {
    font-size: 1.75rem;
    margin-bottom: 1.5rem;
}

/* Quick Actions */
.quick-actions {
    margin-bottom: 2rem;
}

.actions-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
}

.action-card {
    background: rgba(40, 40, 40, 0.95);
    border-radius: 15px;
    padding: 1.5rem;
    text-align: center;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    text-decoration: none;
    color: inherit;
    transition: all 0.3s ease;
}

.action-card:hover {
    transform: translateY(-5px);
    border-color: rgba(99, 102, 241, 0.3);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.2);
}

.action-icon {
    font-size: 2rem;
    margin-bottom: 0.75rem;
}

.action-card h3 {
    color: #ffffff;
    font-size: 1.125rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.action-card p {
    color: #a0a0a0;
    font-size: 0.95rem;
}

/* Mobile Bottom Padding */
.mobile-bottom-padding {
    height: 80px; /* Bottom nav height on mobile */
    display: none;
}

/* Responsive Design */
@media (max-width: 768px) {
    .main-content {
        padding-top: 60px; /* Mobile top nav height */
    }

    .container {
        padding: 1rem;
    }

    .welcome-card {
        padding: 2rem 1.5rem;
    }

    .welcome-title {
        font-size: 2rem;
    }

    .page-title {
        font-size: 2rem;
    }

    .news-grid {
        grid-template-columns: 1fr;
        gap: 1rem;
    }

    .actions-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 1rem;
    }

    .user-stats {
        grid-template-columns: 1fr;
    }

    .featured-card {
        padding: 1.5rem;
    }

    .featured-card .news-title {
        font-size: 1.375rem;
    }

    .mobile-bottom-padding {
        display: block;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 0.75rem;
    }

    .welcome-card {
        padding: 1.5rem 1rem;
    }

    .welcome-title {
        font-size: 1.75rem;
    }

    .page-title {
        font-size: 1.75rem;
    }

    .actions-grid {
        grid-template-columns: 1fr;
    }

    .action-card {
        padding: 1.5rem;
    }

    .news-card {
        padding: 1.25rem;
    }

    .featured-card {
        padding: 1.25rem;
    }

    .section-header {
        flex-direction: column;
        align-items: flex-start;
        gap: 0.5rem;
    }
}

/* Balance Display */
.balance-display {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    background: rgba(40, 40, 40, 0.95);
    padding: 1rem 2rem;
    border-radius: 50px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    margin-top: 1rem;
    flex-wrap: wrap;
}

.balance-separator {
    color: rgba(255, 255, 255, 0.3);
    margin: 0 0.5rem;
}

.balance-icon {
    font-size: 1.25rem;
}

.balance-amount {
    color: #6366f1;
    font-weight: 700;
    font-size: 1.125rem;
}

public/navigation.css.content:
/* Navigation Styles */

/* Desktop Navigation */
.desktop-nav {
    position: sticky;
    top: 0;
    z-index: 1000;
    background: rgba(30, 30, 30, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    padding: 0 2rem;
}

.nav-container {
    display: flex;
    align-items: center;
    justify-content: space-between;
    max-width: 1200px;
    margin: 0 auto;
    height: 70px;
}

.nav-brand {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.lightning-bolt {
    font-size: 1.5rem;
    background: linear-gradient(45deg, #6366f1, #8b5cf6);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.brand-name {
    font-size: 1.25rem;
    font-weight: 700;
    color: #ffffff;
}

.nav-menu {
    display: flex;
    align-items: center;
    gap: 2rem;
}

.nav-link {
    color: #a0a0a0;
    text-decoration: none;
    font-weight: 500;
    padding: 0.5rem 1rem;
    border-radius: 8px;
    transition: all 0.3s ease;
    position: relative;
}

.nav-link:hover,
.nav-link.active {
    color: #ffffff;
    background: rgba(99, 102, 241, 0.1);
}

.nav-link.active::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 50%;
    transform: translateX(-50%);
    width: 20px;
    height: 2px;
    background: linear-gradient(45deg, #6366f1, #8b5cf6);
    border-radius: 1px;
}

/* Dropdown Styles */
.dropdown {
    position: relative;
}

.dropdown-toggle {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: none;
    border: none;
    cursor: pointer;
}

.chevron-down {
    font-size: 0.75rem;
    transition: transform 0.3s ease;
}

.dropdown:hover .chevron-down {
    transform: rotate(180deg);
}

.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%) translateY(-10px);
    width: 650px;
    max-width: 90vw;
    background: rgba(40, 40, 40, 0.98);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
    backdrop-filter: blur(10px);
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
    z-index: 1001;
    margin-top: 0.5rem;
    padding: 1.5rem;
}

.dropdown:hover .dropdown-menu {
    opacity: 1;
    visibility: visible;
    transform: translateX(-50%) translateY(0);
}

/* Grid Dropdown Styles */
.dropdown-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 2rem;
    width: 100%;
}

.dropdown-section {
    display: flex;
    flex-direction: column;
    min-width: 140px;
}

.dropdown-label {
    color: #6366f1;
    font-size: 0.825rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    padding: 0 0 0.5rem 0;
    margin-bottom: 0.75rem;
    border-bottom: 1px solid rgba(99, 102, 241, 0.2);
    text-align: center;
}

.dropdown-items {
    display: flex;
    flex-direction: column;
    gap: 0.25rem;
}

.dropdown-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    color: #a0a0a0;
    text-decoration: none;
    padding: 0.75rem 0.5rem;
    border-radius: 8px;
    transition: all 0.2s ease;
    font-size: 0.875rem;
    white-space: nowrap;
}

.dropdown-item:hover {
    color: #ffffff;
    background: rgba(99, 102, 241, 0.1);
}

.dropdown-item-icon {
    font-size: 0.9rem;
    width: 16px;
    text-align: center;
}

/* Currency Display */
.nav-currency {
    display: flex;
    gap: 1rem;
}

.currency-item {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: rgba(40, 40, 40, 0.8);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.currency-icon {
    font-size: 1rem;
}

.currency-value {
    color: #ffffff;
    font-weight: 600;
    font-size: 0.9rem;
}

/* Mobile Navigation */
.mobile-nav {
    display: none;
}

.mobile-top-nav {
    position: sticky;
    top: 0;
    z-index: 1000;
    background: rgba(30, 30, 30, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    padding: 0 1rem;
}

.mobile-top-nav .nav-container {
    height: 60px;
    justify-content: space-between;
}

.squad-name {
    color: #a0a0a0;
    font-size: 0.9rem;
    font-weight: 500;
}

.mobile-bottom-nav {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    background: rgba(30, 30, 30, 0.95);
    backdrop-filter: blur(10px);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    padding: 0.5rem 0;
}

.mobile-bottom-nav .nav-item {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.25rem;
    color: #a0a0a0;
    text-decoration: none;
    padding: 0.5rem;
    transition: all 0.3s ease;
    background: none;
    border: none;
    cursor: pointer;
}

.mobile-bottom-nav .nav-item:hover,
.mobile-bottom-nav .nav-item.active {
    color: #6366f1;
}

.nav-icon {
    font-size: 1.25rem;
}

.nav-text {
    font-size: 0.75rem;
    font-weight: 500;
}

/* Bottom Sheet */
.bottom-sheet-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);
    z-index: 1998;
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
}

.bottom-sheet-overlay.active {
    opacity: 1;
    visibility: visible;
}

.bottom-sheet {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 1999;
    background: rgba(30, 30, 30, 0.98);
    backdrop-filter: blur(10px);
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    max-height: 70vh;
    transform: translateY(100%);
    transition: transform 0.3s ease;
}

.bottom-sheet.active {
    transform: translateY(0);
}

.sheet-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 1rem;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    position: relative;
}

.sheet-handle {
    position: absolute;
    top: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 40px;
    height: 4px;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 2px;
}

.sheet-header h3 {
    color: #ffffff;
    font-size: 1.125rem;
    font-weight: 600;
    margin: 0;
}

.sheet-close {
    background: none;
    border: none;
    color: #a0a0a0;
    font-size: 1.25rem;
    cursor: pointer;
    padding: 0.25rem;
    border-radius: 4px;
    transition: all 0.3s ease;
}

.sheet-close:hover {
    color: #ffffff;
    background: rgba(255, 255, 255, 0.1);
}

.sheet-content {
    padding: 1rem;
    max-height: calc(70vh - 80px);
    overflow-y: auto;
}

.sheet-section {
    margin-bottom: 1.5rem;
}

.sheet-section:last-child {
    margin-bottom: 0;
}

.section-label {
    color: #6366f1;
    font-size: 0.875rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    margin-bottom: 0.75rem;
}

.sheet-item {
    display: flex;
    align-items: center;
    gap: 1rem;
    color: #a0a0a0;
    text-decoration: none;
    padding: 1rem;
    border-radius: 12px;
    transition: all 0.3s ease;
    margin-bottom: 0.5rem;
}

.sheet-item:hover {
    color: #ffffff;
    background: rgba(99, 102, 241, 0.1);
}

.item-icon {
    font-size: 1.25rem;
    width: 24px;
    text-align: center;
}

.item-text {
    font-weight: 500;
}

/* Body modifications when sheet is open */
body.sheet-open {
    overflow: hidden;
}

/* Responsive Design */
@media (max-width: 768px) {
    .desktop-nav {
        display: none;
    }

    .mobile-nav {
        display: block;
    }

    .mobile-top-nav .nav-currency {
        gap: 0.5rem;
    }

    .mobile-top-nav .currency-item {
        padding: 0.25rem 0.75rem;
        font-size: 0.85rem;
    }

    .squad-name {
        display: none;
    }
}

@media (max-width: 480px) {
    .mobile-top-nav .nav-container {
        padding: 0 0.5rem;
    }

    .mobile-top-nav .currency-item {
        padding: 0.25rem 0.5rem;
    }

    .currency-value {
        font-size: 0.8rem;
    }
}

/* Tablet adjustments */
@media (min-width: 769px) and (max-width: 1024px) {
    .nav-container {
        padding: 0 1rem;
    }

    .nav-menu {
        gap: 1.5rem;
    }
}

public/styles.css.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #1e1e1e, #2d2d2d);
    color: #ffffff;
    min-height: 100vh;
    line-height: 1.6;
}

.container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    padding: 20px;
}

.main-card, .login-card, .qr-card {
    background: rgba(40, 40, 40, 0.95);
    border-radius: 20px;
    padding: 40px;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
    text-align: center;
    max-width: 500px;
    width: 100%;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.logo {
    margin-bottom: 20px;
}

.lightning-bolt {
    font-size: 3rem;
    background: linear-gradient(45deg, #6366f1, #8b5cf6);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    filter: drop-shadow(0 0 10px rgba(139, 92, 246, 0.3));
}

.brand-title, .login-title, .qr-title {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 10px;
    background: linear-gradient(45deg, #ffffff, #e0e0e0);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.brand-subtitle, .login-subtitle, .qr-subtitle {
    color: #a0a0a0;
    margin-bottom: 30px;
    font-size: 1.1rem;
}

.features {
    margin: 30px 0;
    text-align: left;
}

.feature-item {
    display: flex;
    align-items: center;
    margin-bottom: 15px;
    padding: 10px;
    border-radius: 10px;
    background: rgba(99, 102, 241, 0.1);
    border: 1px solid rgba(99, 102, 241, 0.2);
}

.checkmark {
    color: #10b981;
    font-weight: bold;
    margin-right: 15px;
    font-size: 1.1rem;
}

.buttons {
    display: flex;
    flex-direction: column;
    gap: 15px;
    margin-top: 30px;
}

.btn {
    padding: 15px 30px;
    border: none;
    border-radius: 12px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-block;
    text-align: center;
}

.btn-primary {
    background: linear-gradient(45deg, #6366f1, #8b5cf6);
    color: white;
    box-shadow: 0 4px 15px rgba(139, 92, 246, 0.3);
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(139, 92, 246, 0.4);
}

.btn-secondary {
    background: rgba(55, 65, 81, 0.8);
    color: white;
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.btn-secondary:hover {
    background: rgba(75, 85, 99, 0.9);
    transform: translateY(-2px);
}

.btn-full {
    width: 100%;
}

.btn-small {
    padding: 8px 16px;
    font-size: 0.9rem;
}

.btn-danger {
    background: linear-gradient(45deg, #ef4444, #dc2626);
    color: white;
}

.login-form {
    margin-top: 20px;
}

.input-group {
    margin-bottom: 20px;
    text-align: left;
}

.input-group label {
    display: block;
    margin-bottom: 8px;
    color: #e0e0e0;
    font-weight: 500;
}

.input-group input {
    width: 100%;
    padding: 15px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 10px;
    background: rgba(30, 30, 30, 0.8);
    color: white;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.input-group input:focus {
    outline: none;
    border-color: #6366f1;
    box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.1);
}

.input-group input::placeholder {
    color: #888;
}

.code-input {
    text-align: center;
    font-size: 1.5rem;
    letter-spacing: 0.5rem;
    font-weight: bold;
}

.error-message {
    background: rgba(239, 68, 68, 0.1);
    border: 1px solid rgba(239, 68, 68, 0.3);
    color: #fca5a5;
    padding: 12px;
    border-radius: 8px;
    margin-bottom: 20px;
    font-size: 0.9rem;
}

.success-message {
    background: rgba(34, 197, 94, 0.1);
    border: 1px solid rgba(34, 197, 94, 0.3);
    color: #86efac;
    padding: 20px;
    border-radius: 12px;
    margin: 20px 0;
    font-size: 0.9rem;
    text-align: center;
}

.success-message h3 {
    color: #10b981;
    margin-bottom: 10px;
    font-size: 1.2rem;
}

.info-text {
    color: #a0a0a0;
    font-size: 0.9rem;
    margin-top: 15px;
}

.back-link {
    margin-top: 20px;
}

.back-link a {
    color: #6366f1;
    text-decoration: none;
    font-size: 0.9rem;
}

.back-link a:hover {
    text-decoration: underline;
}

/* QR Code Styles */
.qr-container {
    margin: 30px 0;
    padding: 20px;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 15px;
    border: 2px dashed rgba(255, 255, 255, 0.2);
}

.qr-placeholder {
    width: 300px;
    height: 300px;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 10px;
}

.qr-loading {
    text-align: center;
    color: #a0a0a0;
}

.spinner {
    width: 40px;
    height: 40px;
    border: 4px solid rgba(255, 255, 255, 0.1);
    border-left: 4px solid #6366f1;
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin: 0 auto 15px;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.qr-instructions {
    text-align: left;
    margin: 30px 0;
    background: rgba(99, 102, 241, 0.1);
    padding: 20px;
    border-radius: 10px;
    border: 1px solid rgba(99, 102, 241, 0.2);
}

.qr-instructions h3 {
    color: #e0e0e0;
    margin-bottom: 15px;
}

.qr-instructions ol {
    color: #b0b0b0;
    padding-left: 20px;
}

.qr-instructions li {
    margin-bottom: 8px;
}

/* Dashboard Styles */
.dashboard-container {
    min-height: 100vh;
    background: linear-gradient(135deg, #1e1e1e, #2d2d2d);
}

.dashboard-header {
    background: rgba(40, 40, 40, 0.95);
    padding: 20px 30px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
}

.header-left {
    display: flex;
    align-items: center;
    gap: 15px;
}

.header-left h1 {
    font-size: 1.5rem;
    font-weight: 600;
}

.header-left .logo {
    margin: 0;
}

.header-left .lightning-bolt {
    font-size: 1.5rem;
}

.header-right {
    display: flex;
    align-items: center;
    gap: 20px;
}

.user-info {
    color: #a0a0a0;
    font-size: 0.9rem;
}

.dashboard-main {
    padding: 15px;
}

.dashboard-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 12px;
    max-width: 800px;
    margin: 0 auto;
}

.card {
    background: rgba(40, 40, 40, 0.95);
    border-radius: 10px;
    padding: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
}

.card h3 {
    margin-bottom: 10px;
    color: #e0e0e0;
    font-size: 1rem;
}

.status-indicator {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 10px;
}

.status-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: #ef4444;
}

.status-dot.active {
    background: #10b981;
    box-shadow: 0 0 10px rgba(16, 185, 129, 0.3);
}

.user-stats {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.stat {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 6px 0;
