const config = require('../config/config');

/**
 * Extract command and arguments from message text
 * @param {string} messageText - The message text
 * @returns {Object} Command and arguments
 */
const parseCommand = (messageText) => {
    if (!messageText || !messageText.startsWith(config.commands.prefix)) {
        return { command: null, args: [] };
    }

    const parts = messageText.slice(config.commands.prefix.length).trim().split(' ');
    const command = parts[0].toLowerCase();
    const args = parts.slice(1);

    return { command, args };
};

/**
 * Check if message is a command
 * @param {string} messageText - The message text
 * @returns {boolean} Is command status
 */
const isCommand = (messageText) => {
    return messageText && messageText.startsWith(config.commands.prefix);
};

/**
 * Format user mention
 * @param {string} userId - WhatsApp user ID
 * @returns {string} Formatted mention
 */
const formatMention = (userId) => {
    const phoneNumber = userId.split('@')[0];
    return `@${phoneNumber}`;
};

/**
 * Extract phone number from user ID
 * @param {string} userId - WhatsApp user ID
 * @returns {string} Phone number
 */
const extractPhoneNumber = (userId) => {
    return userId.split('@')[0];
};

/**
 * Format currency display
 * @param {number} amount - Amount to format
 * @param {string} currency - Currency type (balance, chips)
 * @returns {string} Formatted currency
 */
const formatCurrency = (amount, currency = 'balance') => {
    const symbols = {
        balance: 'ğŸ’°',
        chips: 'ğŸ¯'
    };

    return `${symbols[currency] || 'ğŸ’°'} ${amount.toLocaleString()}`;
};

/**
 * Format time ago
 * @param {Date} date - Date to format
 * @returns {string} Time ago string
 */
const formatTimeAgo = (date) => {
    const now = new Date();
    const diffInSeconds = Math.floor((now - date) / 1000);

    if (diffInSeconds < 60) {
        return `${diffInSeconds} seconds ago`;
    } else if (diffInSeconds < 3600) {
        const minutes = Math.floor(diffInSeconds / 60);
        return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;
    } else if (diffInSeconds < 86400) {
        const hours = Math.floor(diffInSeconds / 3600);
        return `${hours} hour${hours > 1 ? 's' : ''} ago`;
    } else {
        const days = Math.floor(diffInSeconds / 86400);
        return `${days} day${days > 1 ? 's' : ''} ago`;
    }
};

/**
 * Clean and validate message text
 * @param {string} text - Text to clean
 * @returns {string} Cleaned text
 */
const cleanMessageText = (text) => {
    if (!text) return '';

    return text
        .trim()
        .replace(/\n+/g, '\n') // Remove excessive newlines
        .replace(/\s+/g, ' ') // Remove excessive spaces
        .substring(0, 1000); // Limit length
};

/**
 * Create error message
 * @param {string} error - Error message
 * @param {string} command - Command that caused error
 * @returns {string} Formatted error message
 */
const createErrorMessage = (error, command = '') => {
    return `âŒ Error${command ? ` in command .${command}` : ''}: ${error}`;
};

/**
 * Create success message
 * @param {string} message - Success message
 * @param {string} command - Command that succeeded
 * @returns {string} Formatted success message
 */
const createSuccessMessage = (message, command = '') => {
    return `âœ…${command ? ` .${command}` : ''}: ${message}`;
};

/**
 * Validate WhatsApp user ID format
 * @param {string} userId - User ID to validate
 * @returns {boolean} Is valid format
 */
const isValidUserId = (userId) => {
    if (!userId || typeof userId !== 'string') return false;

    // Check for valid WhatsApp ID format
    const whatsappIdPattern = /^\d+@(s\.whatsapp\.net|g\.us)$/;
    return whatsappIdPattern.test(userId);
};

/**
 * Generate random ID
 * @param {number} length - Length of ID
 * @returns {string} Random ID
 */
const generateRandomId = (length = 8) => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < length; i++) {
        result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
};

module.exports = {
    parseCommand,
    isCommand,
    formatMention,
    extractPhoneNumber,
    formatCurrency,
    formatTimeAgo,
    cleanMessageText,
    createErrorMessage,
    createSuccessMessage,
    isValidUserId,
    generateRandomId
};

utils/stickerUtils.js.content:
const sharp = require('sharp');
const { downloadMediaMessage } = require('@whiskeysockets/baileys');
const fs = require('fs');
const path = require('path');
const config = require('../config/config');
const { Sticker, StickerTypes } = require('wa-sticker-formatter');

// Convert image to sticker using wa-sticker-formatter
const createSticker = async (buffer, pack = config.sticker.pack, author = config.sticker.author) => {
    try {
        console.log(`ğŸ¨ Creating sticker with pack: ${pack}, author: ${author}`);

        // Create sticker using wa-sticker-formatter with proper metadata
        const sticker = new Sticker(buffer, {
            pack: pack,
            author: author,
            type: StickerTypes.FULL,
            quality: 75,
            categories: ['ğŸ¤–', 'ğŸ’¬'],
            id: `seana-bot-${Date.now()}`,
            background: 'transparent'
        });

        // Convert to WebP buffer with metadata
        const stickerBuffer = await sticker.toBuffer();

        console.log(`âœ… Sticker created with metadata - Pack: ${pack}, Author: ${author}`);
        return stickerBuffer;

    } catch (error) {
        console.error('âŒ Error creating sticker with wa-sticker-formatter:', error.message);

        // Fallback: create basic sticker without metadata
        try {
            console.log('ğŸ”„ Trying fallback method...');

            // Create temp directory if it doesn't exist
            const tempDir = path.join(__dirname, '../temp');
            if (!fs.existsSync(tempDir)) {
                fs.mkdirSync(tempDir, { recursive: true });
            }

            const tempInputPath = path.join(tempDir, `input_${Date.now()}.png`);

            // Process image with sharp - resize and convert to PNG
            await sharp(buffer)
                .resize(512, 512, {
                    fit: 'contain',
                    background: { r: 0, g: 0, b: 0, alpha: 0 }
                })
                .webp({ quality: 75 })
                .toFile(tempInputPath);

            const stickerBuffer = fs.readFileSync(tempInputPath);

            // Clean up temp file
            try {
                fs.unlinkSync(tempInputPath);
            } catch (cleanupError) {
                console.log('âš ï¸ Could not clean up temp file:', cleanupError.message);
            }

            console.log('âœ… Created basic sticker without metadata');
            return stickerBuffer;

        } catch (fallbackError) {
            console.error('âŒ Fallback sticker creation failed:', fallbackError.message);
            throw new Error('Failed to create sticker. Please make sure the image is valid.');
        }
    }
};

// Download media from WhatsApp message
const downloadMedia = async (message) => {
    try {
        const buffer = await downloadMediaMessage(
            message,
            'buffer',
            {},
            {
                logger: console,
                reuploadRequest: () => Promise.resolve()
            }
        );
        return buffer;
    } catch (error) {
        console.error('âŒ Error downloading media:', error.message);
        throw new Error('Failed to download media from message.');
    }
};

// Check if message contains image
const isImageMessage = (message) => {
    return !!(
        message.message?.imageMessage ||
        message.message?.viewOnceMessage?.message?.imageMessage ||
        message.message?.ephemeralMessage?.message?.imageMessage
    );
};

// Get image message from different types
const getImageMessage = (message) => {
    if (message.message?.imageMessage) {
        return message.message.imageMessage;
    }
    if (message.message?.viewOnceMessage?.message?.imageMessage) {
        return message.message.viewOnceMessage.message.imageMessage;
    }
    if (message.message?.ephemeralMessage?.message?.imageMessage) {
        return message.message.ephemeralMessage.message.imageMessage;
    }
    return null;
};

module.exports = {
    createSticker,
    downloadMedia,
    isImageMessage,
    getImageMessage
};

utils/userUtils.js.content:
const User = require('../database/models/User');
const config = require('../config/config');

/**
 * Get user from database
 * @param {string} userId - WhatsApp user ID
 * @returns {Object|null} User object or null
 */
const getUser = async (userId) => {
    try {
        const user = await User.findByUserId(userId);
        return user;
    } catch (error) {
        console.error('âŒ Error getting user:', error.message);
        return null;
    }
};

/**
 * Create new user if doesn't exist
 * @param {string} userId - WhatsApp user ID
 * @param {Object} additionalData - Additional user data
 * @param {Object} sock - WhatsApp socket for fetching profile data
 * @returns {Object|null} User object or null
 */
const createUser = async (userId, additionalData = {}, sock = null) => {
    try {
        // Check if user already exists
        let user = await User.findByUserId(userId);

        if (!user) {
            // Extract phone number from userId
            const phoneNumber = userId.split('@')[0];

            // Try to get WhatsApp name if socket is provided
            let whatsappName = `User${phoneNumber.slice(-4)}`;
            if (sock) {
                try {
                    const contactInfo = await sock.onWhatsApp(userId);
                    if (contactInfo && contactInfo[0] && contactInfo[0].notify) {
                        whatsappName = contactInfo[0].notify;
                    }
                } catch (error) {
                    console.log('Could not fetch WhatsApp name during user creation');
                }
            }

            // Set special permissions for owner
            const isOwner = phoneNumber === config.ownerNumber.split('@')[0];
            const userData = {
                phoneNumber,
                username: additionalData.username || whatsappName,
                status: isOwner ? 'owner' : config.defaultSettings.status,
                ...additionalData
            };

            user = await User.createNewUser(userId, userData);
            console.log(`ğŸ‘¤ New user created: ${userId} (${user.status})`);
        }

        return user;
    } catch (error) {
        console.error('âŒ Error creating user:', error.message);
        return null;
    }
};

/**
 * Update user data
 * @param {string} userId - WhatsApp user ID
 * @param {Object} updateData - Data to update
 * @returns {Object|null} Updated user or null
 */
const updateUser = async (userId, updateData) => {
    try {
        const user = await User.findOneAndUpdate(
            { userId },
            { $set: updateData },
            { new: true, runValidators: true }
        );

        if (user) {
            console.log(`ğŸ“ User updated: ${userId}`);
        }

        return user;
    } catch (error) {
        console.error('âŒ Error updating user:', error.message);
        return null;
    }
};

/**
 * Add limit to user
 * @param {string} userId - WhatsApp user ID
 * @param {number} amount - Amount to add
 * @returns {boolean} Success status
 */
const addUserLimit = async (userId, amount = 1) => {
    try {
        const user = await User.findByUserId(userId);
        if (!user) return false;

        user.addLimit(amount);
        await user.save();

        console.log(`â• Added ${amount} limit(s) to user: ${userId}`);
        return true;
    } catch (error) {
        console.error('âŒ Error adding user limit:', error.message);
        return false;
    }
};

/**
 * Deduct limit from user
 * @param {string} userId - WhatsApp user ID
 * @param {number} amount - Amount to deduct
 * @returns {boolean} Success status
 */
const deductUserLimit = async (userId, amount = 1) => {
    try {
        const user = await User.findByUserId(userId);
        if (!user) return false;

        const success = user.deductLimit(amount);
        if (success) {
            await user.save();
            console.log(`â– Deducted ${amount} limit(s) from user: ${userId}`);
        }

        return success;
    } catch (error) {
        console.error('âŒ Error deducting user limit:', error.message);
        return false;
    }
};

/**
 * Check if user is owner
 * @param {string} userId - WhatsApp user ID
 * @returns {boolean} Is owner status
 */
const isUserOwner = async (userId) => {
    try {
        const user = await User.findByUserId(userId);
        return user ? user.isOwner() : false;
    } catch (error) {
        console.error('âŒ Error checking owner status:', error.message);
        return false;
    }
};

/**
 * Check if user is admin or owner
 * @param {string} userId - WhatsApp user ID
 * @returns {boolean} Is admin status
 */
const isUserAdmin = async (userId) => {
    try {
        const user = await User.findByUserId(userId);
        return user ? user.isAdmin() : false;
    } catch (error) {
        console.error('âŒ Error checking admin status:', error.message);
        return false;
    }
};

/**
 * Add XP to user and handle level up
 * @param {string} userId - WhatsApp user ID
 * @param {number} amount - XP amount to add
 * @returns {Object|null} Level up info or null
 */
const addUserXp = async (userId, amount = 10) => {
    try {
        const user = await User.findByUserId(userId);
        if (!user) return null;

        const oldLevel = user.level;
        user.addXp(amount);
        await user.save();

        // Check if user leveled up
        if (user.level > oldLevel) {
            console.log(`ğŸ†™ User ${userId} leveled up! ${oldLevel} â†’ ${user.level}`);
            return {
                leveledUp: true,
                oldLevel,
                newLevel: user.level,
                currentRank: user.getCurrentRank()
            };
        }

        return { leveledUp: false };
    } catch (error) {
        console.error('âŒ Error adding XP:', error.message);
        return null;
    }
};

/**
 * Update user activity and award XP
 * @param {string} userId - WhatsApp user ID
 * @param {string} action - Action type (commandUse, dailyLogin, etc.)
 * @returns {Object|null} Activity update result
 */
const updateUserActivity = async (userId, action = 'commandUse') => {
    try {
        const user = await User.findByUserId(userId);
        if (!user) return null;

        const oldLevel = user.level;

        // Update activity
        user.lastActive = new Date();
        user.commandsUsed += 1;

        // Award XP based on action
        const xpReward = config.levelSystem.rewards[action] || 10;
        user.addXp(xpReward);

        await user.save();

        // Check if user leveled up
        if (user.level > oldLevel) {
            console.log(`ğŸ†™ User ${userId} leveled up! ${oldLevel} â†’ ${user.level}`);
            return {
                leveledUp: true,
                oldLevel,
                newLevel: user.level,
                currentRank: user.getCurrentRank(),
                xpGained: xpReward
            };
        }

        return { 
            leveledUp: false, 
            xpGained: xpReward 
        };
    } catch (error) {
        console.error('âŒ Error updating user activity:', error.message);
        return null;
    }
};

/**
 * Get user statistics
 * @returns {Object} User statistics
 */
const getUserStats = async () => {
    try {
        const totalUsers = await User.countDocuments();
        const activeUsers = await User.countDocuments({
            lastActive: { $gte: new Date(Date.now() - 24 * 60 * 60 * 1000) }
        });
        const ownerCount = await User.countDocuments({ status: 'owner' });
        const adminCount = await User.countDocuments({ status: 'admin' });
        const basicCount = await User.countDocuments({ status: 'basic' });

        return {
            totalUsers,
            activeUsers,
            ownerCount,
            adminCount,
            basicCount
        };
    } catch (error) {
        console.error('âŒ Error getting user stats:', error.message);
        return null;
    }
};

module.exports = {
    getUser,
    createUser,
    updateUser,
    addUserLimit,
    deductUserLimit,
    isUserOwner,
    isUserAdmin,
    getUserStats,
    addUserXp,
    updateUserActivity
};

